package com.android.lq.p2p.lili.base;import android.content.Context;import com.android.lq.p2p.lili.util.PropertiesUtil;import java.util.Properties;/** * 常量类 * * @author 吕强 */public class Constants {    /**     * 易用汇日志输出默认TAG     */    public static final String LOG_TAG = "Aili";    public static final String LICAIZHANGDAN = "licaizhangdan";    public static final String TOUBIAOJILU = "toubiaojilu";    public static final String DENGDAITOUBIAO = "dengdaitoubiao";    public static final String SHOUKUANZHONG = "shoukuanzhong";    public static final String LICAITONGJI = "licaitongji";    public static final String JIQIREN = "jiqiren";    public static final String ZHAIQUANGUANLI = "zhaiquanguanli";    public static final String ZHANGHUXINXI = "zhanghuxinxi";    public static final String ZIJINJILU = "zijinjilu";    public static final String CHONGZHI = "chongzhi";    public static final String TIXIAN = "tixian";    public static final String JISUANQI = "jisuanqi";    public static final String ZHANGHUANQUAN = "zhanghuanquan";    public static final String RECYCLERVIEWADPTER_TYPE = "account";    private static Properties properties;    public static void init(Context context) {        if (properties == null) {            properties = PropertiesUtil.initProperties(context);        }    }    public static String getProperty(String key) {        return properties.getProperty(key, "");    }    public static void writeBoolean(String name, boolean value) {        properties.setProperty(name, ""+value);    }    public static boolean readBoolean(String name, boolean defaultValue) {        return Boolean.parseBoolean(properties.getProperty(name, ""+defaultValue));    }}