package com.android.lq.p2p.lili.util;import android.os.AsyncTask;import android.os.Build.VERSION;/** * 自定义AsyncTask ，解决AsyncTask 顺序执行问题 * @类描述：@param <T> * @类描述：@param <E> * @类描述：@param <B> * @作者：lung * @日期： 2014-3-10 下午4:35:49 * */public abstract class MarketAsyncTask<T, E, B> extends AsyncTask<T, E, B>{    public void doExecutor(T... params)    {        if (Integer.parseInt(VERSION.SDK) >= 11)        {            executeOnExecutor(AsyncTask.THREAD_POOL_EXECUTOR, params);        }        else        {            execute(params);        }    }}